import React, { useContext, useState } from "react";
import Provider from "./Provider";
import Acontext from './Context' 



const Consumer = ()=>{
    const [accept, setAccept] = useState(initialState)

    const mission = ()=>{
    setAccept({status:"Mission Accepted"})
    }
    const a = useContext(Acontext);
    return(
        <div>
            <h3> Hello Mr. Anurag You have Assigned a new mission</h3>
             <h3>Mission: {a.data.mname}</h3>
             
             <h3>Agent Name: {a.data.agent}</h3>
             
             <h3>Status: {a.data.status}</h3>

             <button onClick={mission}>Accept Mission</button>
        </div>
    )
}

export default Consumer;
