import React from 'react';
import { useState, Fragment} from 'react';
import Acontext from './Context';

const Provider = (props) => {
    const [imag, setImag] = useState('')
    const [mission, setMission] = useState({
        userLogged: 'Login to proceed',
        mname: "Rescue the animals from South Africa forest",
        agent: "Captain Anurag",
        status: "Not acceted",
        date: "Accept to know date",
    })
    
    // const disImg = ()=>{
    //     return (
    //         <div>
            
    //         <h1>Hey, R u dere?</h1>
    //         </div>
    //     )
    // }
    
    return (
        <Fragment>
            <Acontext.Provider value={{data:mission,
                accept: ()=>{
                    setMission({...mission, status:"Accepted", date: "30 April 2022", userLogged:"Welcome, You are Logged in", })
                },
                disImag: () =>{
                    setImag({imag: <img src="./download.jpg"/>})
                }
            }}> 
                {props.children}
                </Acontext.Provider>
        </Fragment>
    );
}


export default Provider;
